document.addEventListener("DOMContentLoaded",function(){initAccordion()},!1);function toggleContent(e){let block=e.target.closest(".faq-card__inner");let content=block.querySelector(".faq-card__wrap-content");block.querySelector(".faq-card__btn").classList.toggle("faq-card__btn_show");const isShow=()=>{return+window.getComputedStyle(content).maxHeight.replace("px","")};if(isShow()===0){content.style.maxHeight=content.scrollHeight+"px"}else{content.style.maxHeight=0+"px"}}
function initAccordion(){let faq=document.querySelector("#faq");let list=faq.querySelectorAll(".faq-section__container_desktop .faq-section__list");let cards1=list[0].querySelectorAll(".faq-card");let cards2=list[1].querySelectorAll(".faq-card");cards1.forEach((element,idx)=>{let headerCol1=element.querySelector(".faq-card__header");let headerCol2=cards2[idx].querySelector(".faq-card__header");let h1=window.getComputedStyle(headerCol1).height.replace("px","");console.log("h1: ",h1);let h2=window.getComputedStyle(headerCol2).height.replace("px","");console.log("h2: ",h2);if(h1>h2){headerCol1.style.height=h2+"px"}else if(h1<h2){headerCol2.style.height=h1+"px"}});function addClick(){let titleList=faq.querySelectorAll("div.faq-card__title");let plusBtns=faq.querySelectorAll("button.faq-card__btn");titleList.forEach((element)=>{element.setAttribute("onclick","toggleContent(event)")});plusBtns.forEach((element)=>{element.setAttribute("onclick","toggleContent(event)")})}
addClick()}